import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ClassroomList = () => {
    const [classrooms, setClassrooms] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchClassrooms();
    }, []);

    const fetchClassrooms = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/classrooms');
            setClassrooms(response.data);
            setLoading(false);
        } catch (err) {
            setError('Error fetching classrooms');
            setLoading(false);
        }
    };

    const getFloorColor = (floorNo) => {
        switch(floorNo) {
            case 1: return 'floor-1';
            case 2: return 'floor-2';
            case 3: return 'floor-3';
            default: return '';
        }
    };

    if (loading) return <div className="card">Loading classrooms...</div>;
    if (error) return <div className="card error">{error}</div>;

    return (
        <div className="card">
            <h2>All Classrooms ({classrooms.length})</h2>
            
            <table className="table">
                <thead>
                    <tr>
                        <th>Room ID</th>
                        <th>Capacity</th>
                        <th>Floor</th>
                        <th>Near Washroom</th>
                        <th>Added On</th>
                    </tr>
                </thead>
                <tbody>
                    {classrooms.map(room => (
                        <tr key={room._id}>
                            <td><strong>{room.roomId}</strong></td>
                            <td>{room.capacity} seats</td>
                            <td>
                                <span className={`room-badge ${getFloorColor(room.floorNo)}`}>
                                    Floor {room.floorNo}
                                </span>
                            </td>
                            <td>{room.nearWashroom ? 'Yes' : 'No'}</td>
                            <td>{new Date(room.createdAt).toLocaleDateString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            
            {classrooms.length === 0 && (
                <div style={{ textAlign: 'center', padding: '2rem', color: '#666' }}>
                    No classrooms added yet. Add some classrooms to get started.
                </div>
            )}
        </div>
    );
};

export default ClassroomList;
